class Solution {
public:
    bool canChange(string start, string target) {
        if(start.size()!=target.size()) return false;
        stack<int> st1, st2;
        for(int i=0; i<start.size(); i++){
            if(start[i]!='_') st1.push(start[i]);
            if(target[i]!='_') st2.push(target[i]);
        }
        if(st1.size()!=st2.size()) return false;
        while(st1.empty()==false){
            if(st1.top()!=st2.top()) return false;
            st1.pop();
            st2.pop();
        }
        //l can be in left index as compared to start
        //r can be in right index as compared to start
        vector<int> lefts, rights, leftt, rightt;
        for(int i=0; i<start.size(); i++){
            if(start[i]=='L') lefts.push_back(i);
            if(target[i]=='L') leftt.push_back(i);
            if(start[i]=='R') rights.push_back(i);
            if(target[i]=='R') rightt.push_back(i);
