class Solution {
public:
    bool canChange(string start, string target) {

        string s = start;
        string t = target;

        int s_i = 0;
        int t_i = 0;
        while(s_i < s.size() && t_i < t.size()){
            if(s[s_i] == t[t_i]){
                s_i++;
                t_i++;
            }
            else if(s[s_i] == 'R'){
                s_i++;
            }
            else if(t[t_i] == 'R'){
                t_i++;
            }
            else if(s[s_i] == 'L') return false;

            else if(s[s_i] == '_'){
                int i = s_i;
                while(s_i < s.size() && s[s_i] == '_') s_i++;
                if(s_i == s.size() || s[s_i] == 'R') return false;
                if(s[s_i] == 'L'){
                    swap(s[s_i], s[i]);
                    s_i = i + 1;
                    t_i++;
                }
            }

        }
        s_i = t_i = s.size()-1;
        while(s_i >= 0 && t_i >= 0){
            if(s[s_i] == t[t_i]){
                s_i--;
                t_i--;
            }
            else if(s[s_i] == 'L'){
                s_i--;
            }
            else if(t[t_i] == 'L'){
                t_i--;
            }
            else if(s[s_i] == 'R') return false;
            else if(s[s_i] == '_'){
                int i = s_i;
                while(s_i >= 0 && s[s_i] == '_') s_i--;
                if(s_i == -1 || s[s_i] == 'L') return false;
                if(s[s_i] == 'R'){
                    swap(s[s_i], s[i]);
                    s_i = i - 1;
                    t_i--;
                }
            }

        }
        return s == t;

    }
};
