    int L_count_start = count(start.begin(), start.end(), 'L');
    int R_count_start = count(start.begin(), start.end(), 'R');
    int L_count_target = count(target.begin(), target.end(), 'L');
    int R_count_target = count(target.begin(), target.end(), 'R');
    if (L_count_start != L_count_target || R_count_start != R_count_target) {
        return false;
    }

    // Check if the pieces in start can be moved to obtain target
    int n = start.size();
    int i = 0, j = 0;
    while (i < n && j < n) {
        while (i < n && start[i] == '_') {
            i++;
        }
        while (j < n && target[j] == '_') {
