            while (t < tchar.length && tchar[t] == '_') t++;
            char sch = s < schar.length? schar[s] : ' ';
            char tch = t < tchar.length? tchar[t] : ' ';
            if (sch != tch) return false;
            if (tch == 'R' && s > t) return false;
            if (tch == 'L' && s < t) return false;
            s++; t++;
        }
        return true;
