class Solution {
public:
    bool canChange(string s, string t) {
        stack<int> ind;
        for(int i=0;i<s.length();i++){
            if(s[i]=='R'){
                if(t[i]=='L')
                    return 0;
                if(t[i]=='_')
                    ind.push(i);
            }

            if(t[i]=='R' and s[i]=='_'){
                if(ind.size()==0)
                    return 0;
                s[ind.top()]='_';
                ind.pop();
                s[i]='R';
            }

            if(s[i]=='L')
                if(ind.size())
                    return 0;
        }

        if(ind.size())
            return 0;

        for(int i=s.length()-1;i>=0;i--){
            if(s[i]=='L'){
                if(t[i]=='R')
                    return 0;
                if(t[i]=='_')
                    ind.push(i);
            }

            if(t[i]=='L' and s[i]=='_'){
                if(ind.size()==0)
                    return 0;
                s[ind.top()]='_';
                ind.pop();
                s[i]='L';
            }

            if(s[i]=='R')
                if(ind.size())
                    return 0;
        }

        if(ind.size())
            return 0;

        return s==t;
    }
};
