    long long int ans=0;    // initialize a variable to store the answer
    nums.push_back(1);      // append a dummy value to the end of the vector to handle the last subarray
    for(int i=0;i<nums.size();i++){   // loop through the vector

        if(nums[i]==0){    // if the current element is zero
        cnt++;            // increment the counter

        }
        else if(nums[i]!=0&&cnt!=0){   // if the current element is non-zero and the counter is non-zero
         ans+=(cnt*(cnt+1))/2;   // calculate the number of zero-filled subarrays that end at the previous non-zero element and add it to the answer
         cnt=0;                   // reset the counter
         }

    }
    return ans;    // return the final answer
}


};
//Note that a dummy value of 1 is appended to the end of the vector to handle the last subarray, which may or may not be zero-filled.

