public:
    long long zeroFilledSubarray(vector<int>& nums) {                
        long long ans=0;
        int c=0;
        for(int i=0;i<nums.size();i++) {
            if(nums[i]==0) 
                c++;
            else {
                ans+=(0.5*c*(c+1));
                c=0;
            }                
        }        
        if(nums[nums.size()-1]==0)  
            ans+=(0.5*c*(c+1));
        return ans; 
