class Solution {
public:
    long long zeroFilledSubarray(vector<int>& nums) {
        long long ans=0;
        for(int i=0;i<nums.size();i++){
            if(nums[i]==0){
                long long len=1;
                int j=i+1;
                while(j<nums.size() && nums[j]==0) j++;
                len=j-i;
                long long value=(len*(len+1))/2;
                ans+=value;
                i=j-1;
            }
        }
        return ans;
    }
};
```

