func zeroFilledSubarray(nums []int) int64 {
    nums = append(nums, 1)
    var sum, zeros int64 = 0, 0

    for _, n := range nums {
        if n == 0 {
            zeros++
        } else if zeros > 0 {
            // Add sum of an arithmetic sequence
            // n + n-1 + n-2 ... + 1 => n * (n+1) / 2
            // Where acc - number of zeros in serie
            sum += zeros * (zeros + 1) / 2
            zeros = 0
        }
    }

    return sum
}
```

