class Solution 
{
    public long zeroFilledSubarray(int[] nums) 
    {
        long ans = 0;
        long currCount = 0;

        for(int num : nums)
        {
            if(num != 0)
            {
                ans += count (currCount);
                currCount = 0;
            }

            else
                currCount++;
        }

        //exited out of traversing, but the last part were zeroes
        if(currCount != 0)
            ans += count (currCount);

        return ans;
    }

    //n here is number of zeroes
    static long count(long n)
    {  
        return n*(n+1)/2;
    }  
}
