              while pointerTwo < nums.count && nums[pointerTwo] == 0 {
                  pointerOne += 1
                  pointerTwo += 1
                  counter += 1
              }
              totalCount += counter * (counter + 1) / 2
          }
          pointerOne += 1
      }
