        let mut i     = 0;
        let mut j     = 0;
        let mut n     = nums.len();

        while i < n {
            while i < n && nums[i] != 0 { i += 1; }
            j = i;
            while i < n && nums[i] == 0 { i += 1; }

            subas += sum_of_series((i - j) as i64);
        }
        subas
    }
}
