class Solution {
public:
    long long zeroFilledSubarray(vector<int>& nums) \
    {
        ios_base::sync_with_stdio(false);
        cin.tie(NULL);

        long long c=1;
        int n = nums.size();
        long long ans = 0;

        for(int i=0; i<n-1; i++)
        {
            if(nums[i] == 0 && nums[i+1] == 0)
            c++;

            else if(nums[i] == 0 && nums[i+1] != 0)
            {
                ans += c*(c+1)/2;
                c=1;
            }
        }
        if(nums[n-1] == 0)
        ans += c*(c+1)/2;

        return ans;
    }
};

