    public TreeNode lowestCommonAncestor(TreeNode root, TreeNode p, TreeNode q) {
        while (((long)root.val - p.val) * ((long)root.val - q.val) > 0) { // root.val is not between p.val and q.val.
            root = root.val > p.val ? root.left : root.right; // search LCA.
        }
        return root; // found.
    }
