        while(root != NULL){
            // If both nodes have smaller value than root, go left
            if(root->val > p->val && root->val > q->val)
                root = root->left;
            // If both nodes have higher value than root, go right
            else if(root->val < p->val && root->val < q->val)
                root = root->right;
            else 
                break;
        }
        return root;
    }
