// from https://www.boost.org/doc/libs/1_55_0/doc/html/hash/reference.html#boost.hash_combine
template <class T>
inline void hash_combine(std::size_t& seed, const T& v)
{
    std::hash<T> hasher;
    seed ^= hasher(v) + 0x9e3779b9 + (seed << 6) + (seed >> 2);
}

template<class T>
struct std::hash<const vector<T>>
{
    std::size_t operator()(const vector<T>& v) const noexcept
    {
        std::size_t hash = size(v);
        for (const T& a : v) hash_combine(hash, a);
        return hash;
    }
};
