//Build own Hash and own unordered_map<vector<int>, int> as hash table
public:
    struct MyHash
    {
        size_t operator()(const vector<int>& nums) const
        {
            size_t digest=0;
            for (int x: nums)
                digest^=hash<int>{}(x);
            return digest; 
        }
    };
    int n;
    unordered_map<vector<int>, int, MyHash> Hash;

    int equalPairs(vector<vector<int>>& grid) {
        n=grid.size();
        for(const vector<int>& nums: grid){
            if (Hash.count(nums)==0) Hash[nums]=1;
