        int ans=0;
        for(int i=0; i<n ;i++){
            vector<int> col(n, 0);
            for(int j=0; j<n; j++){
                col[j]=grid[j][i];
            }
            if (Hash.count(col)>0)
                ans+=Hash[col];       
        }
        return ans;
    }
};
