        gridset = defaultdict(int)
        rotatedgrid = list(zip(*grid))
        ans = 0
        for row in grid :
            gridset[tuple(row)] += 1
        for column in rotatedgrid :
            if column in gridset :
                ans += gridset[tuple(column)]
        return ans
