class Solution {
public:
    int equalPairs(vector<vector<int>>& grid) {

        int n = grid.size();
        int ct = 0;

        vector<vector<int>>coli;

        vector<int> v;
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<n;j++)
            {
                v.push_back(grid[j][i]);
            }
            coli.push_back(v);
            v.clear();
        }

        for(int i=0;i<n;i++)
        {
            for(int j=0;j<n;j++)
            {
                if(coli[i]==grid[j]) ct += 1;
            }
        }
        return ct;
    }
};
