    // Function to check if all elements in a row and column are equal to grid[row][col]
    bool check(vector<vector<int>>& grid, int row, int col) {
        int size = grid.size();
        int r1 = 0;
        int c1 = 0;

        // Compare each element in the row with corresponding element in the column
        while (r1 < size) {
            if (grid[row][c1] != grid[r1][col]) {
                return false; // If any element is not equal, return false
            }
            r1++;
            c1++;
        }

        return true; // All elements in the row and column are equal
    }

    int equalPairs(vector<vector<int>>& grid) {
        int ans = 0;

        // Iterate through each element in the grid
        for (int i = 0; i < grid.size(); i++) {
            for (int j = 0; j < grid.size(); j++) {
                if (check(grid, i, j)) {
                    ans++; // If all elements in the row and column are equal, increment the count
                }
            }
        }

        return ans; // Return the total count of equal pairs
    }
};

