class Solution {
public:
    int equalPairs(vector<vector<int>>& grid) {
        int count = 0;
        int len = grid.size();
        for (int i=0; i<len; i++){
            for (int j=0; j<len; j++) {
                bool match = true;
                for (int k=0; k<len; k++) {
                    if (grid[i][k] != grid[k][j]) {match = false; break;}
                }
                count += match ? 1 : 0;
            }
        }
        return count;
    }
};

```

