# Code
```
type Trie struct {
    Count int
    Children map[int]*Trie
}

func equalPairs(grid [][]int) int {
    root := &Trie{Children: make(map[int]*Trie)}

    m := len(grid)
    n := len(grid[0])

    // O(n) * O(m)
    for c := 0; c < n; c++ {
        insert(root, c, m, grid)
    }

    count := 0
    // O(m) * O(n)
    for r := 0; r < m; r++ {
        count += search(root, r, n, grid)
    }

    return count
}

// O(m)
func insert(node *Trie, c, m int, grid [][]int) {
    for r := 0; r < m; r++ {
        idx := grid[r][c]
        if _, ok := node.Children[idx]; !ok {
            node.Children[idx] = &Trie{Children: make(map[int]*Trie)}
        }
        node = node.Children[idx]
    }
    node.Count++
}

// O(n)
func search(node *Trie, r, n int, grid [][]int) int {
    for c := 0; c < n; c++ {
        idx := grid[r][c]
        if _, ok := node.Children[idx]; !ok {
            return 0
        }
        node = node.Children[idx]
    }
    return node.Count
}
```

