```c++
const int base = 5119;
const int mod = 1e9 + 7;

class Solution {
 public:
  int equalPairs(vector<vector<int>>& grid) {
    int n = grid.size();
    vector<long long> row_hash(n);

    int res = 0;

    // rows
    for (int i = 0; i < n; i++) {
      long long h = 0;
      for (int j = 0; j < n; j++) h = (h * base + grid[i][j]) % mod;
      row_hash[i] = h;
    }

    // cols
    for (int j = 0; j < n; j++) {
      long long h = 0;
      for (int i = 0; i < n; i++) h = (h * base + grid[i][j]) % mod;

      for (int i = 0; i < n; i++) res += int(row_hash[i] == h);
    }

    return res;
  }
};
```

