class Solution {
public:
    string mapstr(vector<int>& row){
        string str = "";
        for(auto it : row){
           str+=to_string(it);
           str+="#";
        }
        return str;
    }
    int equalPairs(vector<vector<int>>& grid) {
        unordered_map<string,int>m;

        for(vector<int> row : grid){
            m[mapstr(row)]++;
        }
        int result = 0;

        for(int j = 0 ; j < grid[0].size() ; j++){
            string finder = "";
            for(int i = 0 ; i < grid.size() ; i++){
                finder+=to_string(grid[i][j]);
                finder+="#";
            }
            result+=m[finder];
        }
        return result;
    }
};
```

