class Solution {
public:
    int equalPairs(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        map<vector<int>, int> um;
        // creating a map of row
        for(int i=0;i<n;i++){
            um[grid[i]]++;
        }

        int count=0;
        for(int i=0;i<m;i++){
            vector<int> vec;
            // creating a vector of columns
            for(int j=0;j<n;j++){
                vec.push_back(grid[j][i]);
            }
            // adding the corresponding value of keys into count; 
            count+=um[vec];
        }
        return count;
    }
};
```

