class Solution {
public:
    int equalPairs(vector<vector<int>>& grid) {
        int count = 0;
        int len = grid.size();

        unordered_map <string, int> mymap;
        for (int i=0; i<len; i++) {
            string row = "";
            for (int j=0; j<len; j++) {
                row += to_string(grid[i][j]) + " "; // We add " " in order to handle cases like "1 11" and "11 1".
            }
            mymap[row]++;
        }

        for (int j=0; j<len; j++) {
            string col = "";
            for (int k=0; k<len; k++) {
                col += to_string(grid[k][j]) + " ";
            }
            if (mymap.find(col) != mymap.end()) count += mymap[col];
        }


        return count;
    }
};
