    def equalPairs(self, grid: List[List[int]]) -> int:
        mapping = defaultdict(lambda : 0)
        count = 0
        n = len(grid)
        for row in grid:
            mapping[str(row)] += 1

        for i in range(n):
            col = []
            for j in range(n):
                col.append(grid[j][i])
            count += mapping[str(col)]
        return count
