//custom comparator
struct Compare
{
    bool operator ()( const std::pair<int, string> &a, 
                      const std::pair<int, string> &b ) const{
        return a.first!=b.first?a.first>b.first:a.second<b.second;
    }
};

//f_t_r means food to ratings mapping
//f_t_c means food to cuisine mapping
//c_t_r_f means cuisine to pair<ratings,food> mapping

class FoodRatings {
public:
    unordered_map<string,int> f_t_r;
    unordered_map<string,string> f_t_c;
    unordered_map<string,set<pair<int,string>,Compare>> c_t_r_f;
    FoodRatings(vector<string>& foods, vector<string>& cuisines, vector<int>& ratings) {
        for(int i = 0;i < foods.size();i++){
            f_t_c[foods[i]] = cuisines[i];
            f_t_r[foods[i]] = ratings[i];
            c_t_r_f[cuisines[i]].insert(make_pair(ratings[i],foods[i]));
        }
    }

    void changeRating(string food, int newRating) {
        string cuisine = f_t_c[food];
        int old_ratings = f_t_r[food];

        f_t_r[food] = newRating;
        c_t_r_f[cuisine].erase(make_pair(old_ratings,food));
        c_t_r_f[cuisine].insert(make_pair(newRating,food));
    }

    string highestRated(string cuisine) {
        return c_t_r_f[cuisine].begin()->second;
    }
};

