class FoodRatings {
	// custom class to store information of the food
    class info{
        public:
            int rating;
            string food_name;

        info(int r,string s) //constructor
        {
            rating = r;
            food_name=s;
        }
    };

	//custom comparator to maintan ordering in set...
    class comp{
        public:
            bool operator()(info a,info b)const
            {
                if(a.rating==b.rating)
                    return a.food_name<b.food_name;
                return a.rating>b.rating;
            }
    };

    map<string,string> which_cuisine; // mapping food with corresponding cuisine
    map<string,set<info,comp>> cuisine_info; // storing food information and mapping it to the corresponding cuisine
    map<string,int> curr_r; // a map to store the current rating of the food... 

public:
    FoodRatings(vector<string>& foods, vector<string>& cuisines, vector<int>& ratings) {
        for(int i=0;i<foods.size();i++)
        {
            curr_r[foods[i]]=ratings[i];
            which_cuisine[foods[i]]=cuisines[i];
            cuisine_info[cuisines[i]].insert(info(ratings[i],foods[i]));
        }
    }

    void changeRating(string food, int newRating) {
		//getting the current rating of the food
        int old_r=curr_r[food]; 
        string cuisine = which_cuisine[food];

        cuisine_info[cuisine].erase(cuisine_info[cuisine].find(info(old_r,food))); //removing the food previous rating
        cuisine_info[cuisine].insert(info(newRating,food));
        curr_r[food]=newRating; //updating the current rating...
    }

    string highestRated(string cuisine) {

        info temp = *cuisine_info[cuisine].begin();
        return temp.food_name;
    }
};

