from collections import defaultdict
import heapq

class FoodRatings:

    def __init__(self, foods: List[str], cuisines: List[str], ratings: List[int]):
        n = len(foods)
        self.foods = {foods[i]:ratings[i] for i in range(n)}
        self.cuisines = {foods[i]:cuisines[i] for i in range(n)}
        self.cuisine_rating = defaultdict(list)
        for i in range(n):
            heapq.heappush(self.cuisine_rating[cuisines[i]], (-1 * ratings[i], foods[i]))

    def changeRating(self, food: str, newRating: int) -> None:
        self.foods[food]=newRating
        heapq.heappush(self.cuisine_rating[self.cuisines[food]], (-1 * newRating, food))

    def highestRated(self, cuisine: str) -> str:
        while  self.cuisine_rating[cuisine]:
            if -1 * self.cuisine_rating[cuisine][0][0] == self.foods[self.cuisine_rating[cuisine][0][1]]:
                return self.cuisine_rating[cuisine][0][1]
            else:
                heapq.heappop(self.cuisine_rating[cuisine])
