    public class FoodRatings
    {
        private readonly Dictionary<string, int> dict;
        private readonly Dictionary<string, string> set;
		//we donot design a new class, but using a string[2], string[0] is foodname, string[1] is rate.ToString()
        private readonly Dictionary<string, PriorityQueue<string[], string[]>> map;
        public FoodRatings(string[] foods, string[] cuisines, int[] ratings)
        {
            dict = new Dictionary<string, int>();
            set = new Dictionary<string, string>();
            map = new Dictionary<string, PriorityQueue<string[], string[]>>();
            int n = foods.Length;
            for (int i = 0; i < n; i++)
            {
                dict.Add(foods[i], ratings[i]);//store {food,rating} pairs
                set.Add(foods[i], cuisines[i]);//store {food,cursine} pairs
                if (!map.ContainsKey(cuisines[i]))
                    map.Add(cuisines[i], new PriorityQueue<string[], string[]>(Comparer<string[]>.Create((x, y) =>
                    {
                        var rate1 = int.Parse(x[1]);
                        var rate2 = int.Parse(y[1]);
                        if (rate1 > rate2) return -1;//return better rating food of this cuisine type
                        else if (rate1 < rate2) return 1;
                        else return x[0].CompareTo(y[0]);//if two same rating existed, return smaller name
                    })));
				//store string[]{ foods[i], ratings[i].ToString()} pairs
                map[cuisines[i]].Enqueue(new string[] { foods[i], ratings[i].ToString() }, new string[] { foods[i], ratings[i].ToString() });
            }
        }

        public void ChangeRating(string food, int newRating)
        {
            dict[food] = newRating;
            var cuisine = set[food];
			//every time changing rating, Enqueue new one in map, old still in map but expired!
            map[cuisine].Enqueue(new string[] { food, newRating.ToString() }, new string[] { food, newRating.ToString() });
        }

        public string HighestRated(string cuisine)
        {
            var pq = map[cuisine];
            while (pq.Count > 0)
            {
                var top = pq.Peek();
                var rate = int.Parse(top[1]);
                if (dict[top[0]] == rate) return top[0];//if current rating is correct, return top[0] as food name
                else pq.Dequeue();//if rating of this food - top[0] was changed, the discard this expired one
            }
            return "";//never go here
        }
    }

