class FoodRatings:

    def __init__(self, foods: List[str], cuisines: List[str], ratings: List[int]):
        self.bst = defaultdict(SortedSet)
        self.food = {}
        self.cuisine = {}
        for f, c, r in zip(foods, cuisines, ratings):
            self.cuisine[f] = c
            self.food[f] = r
            self.bst[c].add((-r, f))

    def changeRating(self, food: str, newRating: int) -> None:
        self.bst[self.cuisine[food]].remove((-self.food[food], food))
        self.food[food] = newRating
        self.bst[self.cuisine[food]].add((-newRating, food));

    def highestRated(self, cuisine: str) -> str:
        return self.bst[cuisine][0][1]
