public class FoodRatings {

 // custom comparer class is required because we want to sort our red-black tree or sorted set 
//in such a way that lexicographical ordering is followed.
     class Cmp:IComparer<(int r, string f)> {
        public int Compare((int r, string f) o1, (int r, string f) o2) {
            if(o1.r > o2.r) return 1;
            else if(o1.r < o2.r) return -1;
            else {
                return o2.f.CompareTo(o1.f);
            }
        }
    }

    private Dictionary<string, SortedSet<(int rating, string food)>> map;
    private Dictionary<string, int> mapFood;
    private Dictionary<string ,string> mapFoodKit;
    public FoodRatings(string[] foods, string[] cuisines, int[] ratings) {
        map = new Dictionary<string, SortedSet<(int, string)>>();
        mapFood = new Dictionary<string, int>();
         mapFoodKit = new Dictionary<string, string>();
        for(int i=0;i<foods.Length;i++){
             mapFood[foods[i]] = ratings[i];
            mapFoodKit[foods[i]] = cuisines[i];
            if(map.ContainsKey(cuisines[i])){
                 map[cuisines[i]].Add((ratings[i], foods[i]));
            }
            else{
                SortedSet<(int rating, string food)> s = new SortedSet<(int, string)>(new Cmp());
                s.Add((ratings[i], foods[i]));
                map.Add(cuisines[i], s);
            }
        }
    }

    public void ChangeRating(string food, int newRating) {
        if(mapFood.ContainsKey(food)){
            int ir = mapFood[food];
            mapFood[food] = newRating;
            string cuisine = mapFoodKit[food];
            var s = map[cuisine];
             s.Remove((ir, food));
             s.Add((newRating, food));
        }
    }

    public string HighestRated(string cuisine) {
        var s = map[cuisine].Max;
        return s.food;
    }
}

