from sortedcontainers import SortedList

class FoodRatings:
    def __init__(self, foods: List[str], cuisines: List[str], ratings: List[int]):
        self.ratings = defaultdict(SortedList) # c --> [(-r, f), ...]
        self.frc = {} # f --> (c, r)
        for f, c, r in zip(foods, cuisines, ratings):
            self.ratings[c].add((-r, f))
            self.frc[f] = c, r

    def changeRating(self, food: str, newRating: int) -> None:
        cuisine, oldRating = self.frc[food]
        self.ratings[cuisine].remove((-oldRating, food))
        self.ratings[cuisine].add((-newRating, food))       
        self.frc[food] = (cuisine, newRating)

    def highestRated(self, cuisine: str) -> str:
        return self.ratings[cuisine][0][1]
