class FoodRatings {

    //Used to hold [food_name ==> cuisine_type]
    private Map<String, String> typeOf;

    //Used to hold [cuisine_type => [(food_name, food_rating)]]
    private Map<String, TreeSet<Pair<String, Integer>>> cuisinesMap;

    //Used to hold food_name -> reference_of_food_info_in_treeSet
    private Map<String, Pair<String, Integer>> foodToInfo;

    public FoodRatings(String[] foods, String[] cuisines, int[] ratings) {
        cuisinesMap = new HashMap<>();
        foodToInfo = new HashMap<>();
        typeOf = new HashMap<>();

        for(int i = 0; i < foods.length; i += 1) {
            String foodName = foods[i], foodStyle = cuisines[i];
            int foodRating = ratings[i];
            cuisinesMap.putIfAbsent(foodStyle, new TreeSet<>((a, b) -> {
                Integer r1 = a.second, r2 = b.second;
                if(r1.equals(r2)) return a.first.compareTo(b.first); 
                return Integer.compare(r2, r1);
            }));
            Pair<String, Integer> foodInfo = new Pair<>(foodName, foodRating);
            typeOf.put(foodName, foodStyle);
            cuisinesMap.get(foodStyle).add(foodInfo);
            foodToInfo.put(foodName, foodInfo);
        }
    }

    public void changeRating(String food, int newRating) {
        Pair<String, Integer> foodInfo = foodToInfo.get(food);
        String foodStyle = typeOf.get(food);
         //removal & addition required to re-arrange the data in TreeSet
        cuisinesMap.get(foodStyle).remove(foodInfo);
        foodInfo.second = newRating;
        cuisinesMap.get(foodStyle).add(foodInfo);
    }

    public String highestRated(String cuisine) {
        return cuisinesMap.get(cuisine).first().first;
    }

    private static class Pair<T1, T2> {
		T1 first;
		T2 second;

		Pair(T1 _first, T2 _second) {
			first = _first;
			second = _second;
		}

		@Override
		public String toString() {
			return "[" + first.toString() + "," + second.toString() + "]";
		}

		@Override
		public int hashCode() {
			return 31 * second.hashCode() + first.hashCode();
		}

		@Override
		public boolean equals(Object obj) {
			if(obj == null || obj.getClass() != this.getClass()) return false;
			if(obj == this) return true;
			Pair<?, ?> p = (Pair)obj;
			return this.first.equals(p.first) && this.second.equals(p.second);
		}
	}
}

