class FoodRatings {
    class Food{
        String foodItem;
        String cuisine;
        int rating;
        Food(String foodItem,String cuisine,int rating){
            this.foodItem = foodItem;
            this.cuisine = cuisine;
            this.rating = rating;
        }
    }
    HashMap<String,Food> foodItems;
    HashMap<String,PriorityQueue<Food>> highestRatingFood;    

    public FoodRatings(String[] foods, String[] cuisines, int[] ratings) {
        foodItems = new HashMap<>();
        highestRatingFood = new HashMap<>();

        for(int i=0;i<foods.length;i++){
            Food food = new Food(foods[i],cuisines[i],ratings[i]);
            foodItems.put(foods[i],food); // Since its gaurenteed all foods[i] is unique, directly adding in the hashmap.
            PriorityQueue<Food> pq;
            if(highestRatingFood.containsKey(cuisines[i]))
                pq = highestRatingFood.get(cuisines[i]);
            else
                pq = new PriorityQueue<>((a,b)->b.rating == a.rating ? a.foodItem.compareTo(b.foodItem) : b.rating-a.rating); // Desc Order.
            pq.add(food);
            highestRatingFood.put(cuisines[i],pq);
        }
    }
    public void changeRating(String food, int newRating) {
        Food prevHistory = foodItems.get(food);
        PriorityQueue<Food> pq = highestRatingFood.get(prevHistory.cuisine);
        pq.remove(prevHistory); //Removing the old value
        prevHistory.rating = newRating;  //Updating Rating
        pq.add(prevHistory); //Adding it back
        highestRatingFood.put(prevHistory.cuisine,pq); // Updating cuising
        foodItems.put(food,prevHistory); // Not needed yet updated.
    }
    public String highestRated(String cuisine) {
        return highestRatingFood.get(cuisine).peek().foodItem;
    }    
}

