    //used treeSet so we always get highest priority food  ( Treating it like priorit queue)
    //used Custom Food class which implements Comparable<T> interface, compareTo Method is written keeping in mind that, food should be sorted based on desc order of rating and then as per lexicographical order of food name
    HashMap<String, TreeSet<Food>> cuisineToFoods = new HashMap<>();

    //this hashMap maps food to cuisine
    HashMap<String, String> foodToCuisine = new HashMap<>();

    //this hashMap maps food to its rating
    HashMap<String, Integer> foodToRating = new HashMap<>();

    //initializes above defined maps
    public FoodRatings(String[] foods, String[] cuisines, int[] ratings) {
        for(int i = 0; i < foods.length; i++){
            foodToCuisine.put(foods[i], cuisines[i]);

            Food f = new Food(foods[i], ratings[i]);

            TreeSet<Food> tempSet = cuisineToFoods.getOrDefault(cuisines[i], new TreeSet<>());
            tempSet.add(f);
            cuisineToFoods.put(cuisines[i], tempSet);
            foodToRating.putIfAbsent(foods[i], ratings[i]);
        }
    }

    // takes out food from tempSet (TreeSet) O(Log(N)) timeComplexity
    // adds food with new Rating to tempSet o(Log(N)) timeComplexity
    public void changeRating(String food, int newRating) {
        String cuisine = foodToCuisine.get(food);
        TreeSet<Food> tempSet = cuisineToFoods.get(cuisine);

        int rating = foodToRating.get(food);
        Food crit = new Food(food,rating);
        //System.out.println("Rating will be removed for food:"+ food +" with crit as follows");
        //System.out.println(crit.name+" "+ crit.rating);
        //System.out.println("size before"+ tempSet.size());
        tempSet.remove(crit);
        //System.out.println("size after"+tempSet.size());
        crit.rating = newRating;
        tempSet.add(crit);
        foodToRating.put(food,newRating);
        return;
    }

    //Retrives in constant time O(1) time complexity
    public String highestRated(String cuisine) {
        TreeSet<Food> tempSet = cuisineToFoods.get(cuisine);

        return tempSet.first().name;
    }
