# Code
```
class Solution {
public:
    int n;  // Variable to store the size of the grid
    unordered_map<string, int> Hash;  // Hash map to store the frequency of each string representation of a vector

    // Function to convert a vector of integers to a string representation
    string to_str(const vector<int>& nums){
        string s="(";  // Initialize the string with an opening parenthesis
        for(int x:nums)
            s+=to_string(x)+",";  // Append each integer to the string followed by a comma
        return s;  // Return the resulting string
    } 

    // Function to calculate the number of equal pairs in the grid
    int equalPairs(vector<vector<int>>& grid) {
        n=grid.size();  // Set the size of the grid

        // Loop through each vector in the grid
        for(const vector<int>& nums: grid){
            string s=to_str(nums);  // Convert the vector to a string representation
            if (Hash.count(s)==0)
                Hash[s]=1;  // If the string representation is not present in the hash map, add it with a frequency of 1
            else
                Hash[s]++;  // If the string representation is already present, increment its frequency
        }

        int ans=0;  // Initialize the answer variable

        // Loop through each column in the grid
        for(int i=0; i<n ;i++){
            vector<int> col(n, 0);  // Initialize a vector to store the elements of the current column
            for(int j=0; j<n; j++){
                col[j]=grid[j][i];  // Store the elements of the column in the vector
            }
            string s=to_str(col);  // Convert the vector to a string representation

            // Check if the string representation exists in the hash map and add its frequency to the answer
            if (Hash.count(s)>0)
                ans+=Hash[s];       
        }

        return ans;  // Return the final answer
    }
};

```
