class Solution {
public:
    int equalPairs(vector<vector<int>>& grid) {
        std::map<std::vector<int>, int> gridRow; // Map to store the frequency of each row
        int ans = 0; // Variable to store the result

        // Count the frequency of each row in the grid
        for (auto row : grid) {
            gridRow[row]++;
        }

        std::vector<int> temp; // Temporary vector to store each column

        // Iterate through each column
        for (int i = 0; i < grid.size(); i++) {
            for (int j = 0; j < grid.size(); j++) {
                temp.push_back(grid[j][i]); // Store the elements of the column in the temporary vector
            }

            if (gridRow.count(temp)) {
                ans += gridRow[temp]; // Increment the result by the frequency of the column in the grid
            }

            temp.clear(); // Clear the temporary vector for the next column
        }

        return ans; // Return the final result
    }
};

