If you are not familiar with `Trie` data structure you can solve [this](https://leetcode.com/problems/implement-trie-prefix-tree/) leetcode problem on trie and see [this](https://youtu.be/oobqoCJlHA0) video
[https://youtu.be/oobqoCJlHA0]()
```
class trieNode{
    public:
    int count;
    unordered_map<int, trieNode*> children;
    trieNode(){
        this->count = 0;
    }
};

class Trie{
    public:
    trieNode* root;
    Trie(){
        this->root = new trieNode;
    }

    void insert(vector<int> &arr){
        trieNode *temp = root;
        for(int num : arr){
            if(temp->children.find(num) != temp->children.end()){
                temp = temp->children[num];
            }else{
                trieNode* newNode = new trieNode();
                temp->children[num] = newNode;
                temp = temp->children[num];
            }
        }
        temp->count++;
    }

    int search(vector<vector<int>> &grid, int n, int col){
        trieNode *temp = root;
        for(int i = 0; i < n; i++){
            if(temp->children.find(grid[i][col]) == temp->children.end()){
                return 0;
            }
            temp = temp->children[grid[i][col]];
        }
        return temp->count;
    }
};

class Solution {
public:
    int equalPairs(vector<vector<int>>& grid) {
        int n = grid.size();
        Trie *myTrie = new Trie();
        for(auto &row : grid){
            myTrie->insert(row);
        }

        int ans = 0;
        for(int col = 0; col < n; col++){
            ans += myTrie->search(grid, n, col);
        }
        delete myTrie;
        return ans;
    }
};
```

