	from sortedcontainers import SortedList
	class FoodRatings:
		def __init__(self, foods: List[str], cuisines: List[str], ratings: List[int]):
			self.f2c = {}
			self.c2rf = defaultdict(SortedList)
			for i in range(len(foods)):
				self.f2c[foods[i]] = (cuisines[i], ratings[i])
				self.c2rf[cuisines[i]].add((-ratings[i], foods[i]))

		def changeRating(self, food: str, newRating: int) -> None:
			cuisine, rating = self.f2c[food]
			self.c2rf[cuisine].remove((-rating, food))
			self.c2rf[cuisine].add((-newRating, food))
			self.f2c[food] = (cuisine, newRating)

		def highestRated(self, cuisine: str) -> str:
			return self.c2rf[cuisine][0][1]

### use heap
	from collections import defaultdict
	from heapq import heappush, heappop
	class FoodRatings:
		def __init__(self, foods: List[str], cuisines: List[str], ratings: List[int]):
			self.f2r = defaultdict(int)
			self.f2c = defaultdict(str)
			self.c2rf = defaultdict(list)
			for i in range(len(foods)):
				self.f2r[foods[i]] = ratings[i]
				self.f2c[foods[i]] = cuisines[i]
				heappush(self.c2rf[cuisines[i]], (-ratings[i], foods[i]))

		def changeRating(self, food: str, newRating: int) -> None:
			self.f2r[food] = newRating
			heappush(self.c2rf[self.f2c[food]], (-newRating, food))

		def highestRated(self, cuisine: str) -> str:
			while (self.c2rf[cuisine]):
				if (self.c2rf[cuisine][0][0] != -self.f2r[self.c2rf[cuisine][0][1]]):
					heappop(self.c2rf[cuisine])
				else:
					return self.c2rf[cuisine][0][1]

