import heapq
class FoodRatings:

    def __init__(self, foods: List[str], cuisines: List[str], ratings: List[int]):
        self.food_ratings = {food:-rating for food, rating in zip(foods, ratings)}

        self.food_cuisine = {food:cuisine for food, cuisine in zip(foods, cuisines)}

        self.cuisine_foods = collections.defaultdict(list)
        for cuisine, (food, rating) in zip(cuisines, zip(foods, ratings)):
            self.cuisine_foods[cuisine].append((-rating, food))
        for cuisine in self.cuisine_foods:
            heapq.heapify(self.cuisine_foods[cuisine])

    def changeRating(self, food: str, newRating: int) -> None:
        self.food_ratings[food] = -newRating
        cuisine = self.food_cuisine[food]
        heapq.heappush(self.cuisine_foods[cuisine], (-newRating, food))

    def highestRated(self, cuisine: str) -> str:
        while self.cuisine_foods[cuisine]:
            rating, food = self.cuisine_foods[cuisine][0]
            if self.food_ratings[food] != rating:
                heapq.heappop(self.cuisine_foods[cuisine])
            else:
                return food

