class dish{
public:
    string food;
    int rating;

    dish(string food, int rating){
        this->food = food;
        this->rating = rating;
    }
};

bool operator<(const dish &d1, const dish &d2){
    if(d1.rating == d2.rating) return d1.food > d2.food;
    return d1.rating < d2.rating;
}

class FoodRatings {
private:
    unordered_map<string, priority_queue<dish>> menuChart;
    unordered_map<string, int> foodRatingMap;
    unordered_map<string, string> foodCuisineMap;

public:
    FoodRatings(vector<string>& foods, vector<string>& cuisines, vector<int>& ratings) {
        int n = foods.size();

        for(int i = 0; i < n; i++){
            string cuisine = cuisines[i];
            string food = foods[i];
            int rating = ratings[i];
            dish d = dish(food, rating);

            menuChart[cuisine].emplace(d);
            foodCuisineMap[food] = cuisine;
            foodRatingMap[food] = rating;
        }
    }

    void changeRating(string food, int newRating) {
        foodRatingMap[food] = newRating;
        string cuisine = foodCuisineMap[food];
        menuChart[cuisine].emplace(dish(food, newRating));
    }

    string highestRated(string cuisine) {
        while(true){
            dish d = menuChart[cuisine].top();
            if(foodRatingMap[d.food] == d.rating) return d.food;
            else menuChart[cuisine].pop();
        }
    }
};
