            final TreeSet<Food> set = cuisineFood.computeIfAbsent(cuisine, c -> new TreeSet<>((f1, f2) -> {
                int diff = f2.rating - f1.rating;
                if (diff != 0) return diff;
                return f1.food.compareTo(f2.food);
