        String cuisine = foodCuisine.get(food);
        TreeSet<Food> set = cuisineFood.get(cuisine);

        set.remove(f);
        f.rating = newRating;
        set.add(f);
    }

    public String highestRated(String cuisine) {
        return cuisineFood.get(cuisine).first().food;
