        for (int i = 0; i < foods.length; i++) {
            foodCuisine.put(foods[i], cuisines[i]);

            map.putIfAbsent(cuisines[i], new PriorityQueue<>((a, b) -> {
                if (((int)a[1]) != (int)b[1]) {
                    return (int)b[1] - (int)a[1]; 
                }

                return ((String)a[0]).compareTo((String)b[0]);
            }));

            PriorityQueue<Object[]> pq = map.get(cuisines[i]);
            pq.offer(new Object[] {foods[i], ratings[i]});

            map.put(cuisines[i], pq);
        }
    }

    public void changeRating(String food, int newRating) {
        String cuisine = foodCuisine.get(food);
        foodRating.put(food, newRating);

        PriorityQueue<Object[]> pq = map.get(cuisine);
		// put the latest rating to pq
        pq.offer(new Object[] {food, newRating});

        map.put(cuisine, pq);

    }

    public String highestRated(String cuisine) {
        PriorityQueue<Object[]> pq = map.get(cuisine);

        String result = null;

        while (!pq.isEmpty()) {
			// get the top result
            Object[] top = pq.peek();
            String food = (String)top[0];
            int rating = (int)top[1];

            result = food;

			// check if we have updated the rating of the food, if the rating is the latest one
            if (!foodRating.containsKey(food) || foodRating.get(food) == rating) {
                break;
            } 
            pq.poll();
        }

        return result;
    }
}

