class FoodRatings {
public:
    //Rating is stored as -ve as to have the largest value at the begining
    //to store data in the format - coisines :{ {-rating1, food1}, {-rating1, food1} ...}
    //This map will be used to get the highest rating
    map<string, set<pair<int, string>>> smp; 

    //to store maping of food to coisine type ( food -> coisine)
    map<string, string> tmp;

    // to store maping of last rating of a food ( food -> rating)
    map<string, int> rmp;

    FoodRatings(vector<string>& foods, vector<string>& cuisines, vector<int>& ratings){
        //store all the data
        for(int i = 0; i < foods.size(); ++i){
            smp[cuisines[i]].insert({-ratings[i], foods[i]});
            tmp[foods[i]] = cuisines[i];
            rmp[foods[i]] = ratings[i];
        }
    }

    void changeRating(string food, int newRating) {
        string ctype = tmp[food];
        int oldRating = rmp[food];  //get older rating of the food
        rmp[food] = newRating;      
        smp[ctype].erase({-oldRating, food});  //erase the older rating 
        smp[ctype].insert({-newRating, food}); //iinsert the new one
    }

    string highestRated(string cuisine) {
        auto it = smp[cuisine].begin(); //this will return a pointer to first {-rating, food}
        return (*it).second;
    }
};
