class FoodRatings {
     Map<String, Integer> foodToRating = new HashMap();
  Map<String, String> foodToCuisine = new HashMap();
  Map<String, TreeMap<Integer, Set<String>>> cuisineToRating = new HashMap();

  public FoodRatings(String[] foods, String[] cuisines, int[] ratings) {
    for(int i=0; i< foods.length; i++){
      var food = foods[i];
      var rating = ratings[i];
      var cuisine = cuisines[i];
      foodToRating.put(food, rating);
      foodToCuisine.put(food, cuisine);

      cuisineToRating.putIfAbsent(cuisine, new TreeMap(Collections.reverseOrder()));
      cuisineToRating.get(cuisine).putIfAbsent(rating, new TreeSet());
      cuisineToRating.get(cuisine).get(rating).add(food);

    }
  }

  public void changeRating(String food, int newRating) {
    var oldRating = foodToRating.get(food);
           if(oldRating==newRating) return;


    foodToRating.put(food, newRating);
    var cuisine = foodToCuisine.get(food);
    cuisineToRating.get(cuisine).putIfAbsent(newRating, new TreeSet());
    cuisineToRating.get(cuisine).get(newRating).add(food);

    cuisineToRating.get(cuisine).get(oldRating).remove(food);
    if(cuisineToRating.get(cuisine).get(oldRating).size()==0){
      cuisineToRating.get(cuisine).remove(oldRating);
    }
  }

  public String highestRated(String cuisine) {
    Set<String> names = cuisineToRating.get(cuisine).firstEntry().getValue();
    return names.iterator().next();
  }
}

