#define pii pair<int,string>
class Compare
{
public:
    bool operator() (pii p1,pii p2)
    {
        if(p1.first<p2.first)
            return true;
        else if(p1.first==p2.first)
        {
            return (p1.second>p2.second);
        }
        return false;
    }
};
class FoodRatings {
public:
    unordered_map<string,pair<int,string>>m;
    unordered_map<string,priority_queue<pii,vector<pii>,Compare>>mcu;

    FoodRatings(vector<string>& f, vector<string>& c, vector<int>& r) {
        for(int i=0;i<f.size();i++)
        {
            m[f[i]]={r[i],c[i]};
            mcu[c[i]].push({r[i],f[i]});
        }
    }

    void changeRating(string food, int newRating) {
        m[food].first=newRating;
        mcu[m[food].second].push({newRating,food});
    }

    string highestRated(string c) {

        while(!mcu[c].empty())
        {
            auto [rating,food]=mcu[c].top();
            if(m[food].first!=rating)
                mcu[c].pop();
            else
            {
                return food;
            }
        }

        return "";
    }
};

